			this.RotationBox.Text += "C:" + d2.critRating + ",V:" + d2.verRating + ",H:" + d2.hasteRating + ",M:" + d2.masteryRating + ", ";
			this.RotationBox.Text += fs.LongRun_PenanceAndShield(d2, 4, 4).ToString();
			conservativeMPS = fs.ManaSpent / fs.Time;
